!(function () {
  "use strict";
  
  const activePage = window.location.pathname;
  const links = document.querySelectorAll('nav ul a')
  links.forEach(link=>{
    activePage === '/' ? links[0].classList.add("active") : link.href.includes(activePage) ? link.classList.add('active') : ""
  })


  var e = document.querySelector("#mainNav");
  if (e) {
    var n = function () {
      (void 0 !== window.pageYOffset
        ? window.pageYOffset
        : (
            document.documentElement ||
            document.body.parentNode ||
            document.body
          ).scrollTop) > 100
        ? e.classList.add("navbar-shrink")
        : e.classList.remove("navbar-shrink");
    };
    n(), document.addEventListener("scroll", n);
  }
})();

// Get the button:
let myBtn = document.getElementById("myBtn");

// When the user scrolls down 20px from the top of the document, show the button
window.onscroll = function () {
  scrollFunction();
};

function scrollFunction() {
  if (document.body.scrollTop > 20 || document.documentElement.scrollTop > 20) {
    myBtn.style.display = "block";
  } else {
    myBtn.style.display = "none";
  }
}

// When the user clicks on the button, scroll to the top of the document
function topFunction() {
  document.body.scrollTop = 0; // For Safari
  document.documentElement.scrollTop = 0; // For Chrome, Firefox, IE and Opera
}
